//Select a colour based on value
function selectColor(value, threshold, type)
{
    var color = "#000000";

    if (type == 1 && value >= threshold) {
        //color = '#ffa3a3';
        color = '#ab0000';
    }
    else if (type == 2 && value <= threshold) {
        //color = '#ffa3a3';
        color = '#ab0000';
    }
    else 
    {
        return null;
    }

    return color;
}

