function adjustDate(sdate, edate)
{
    var oneDayMsec = 24*60*60*1000;
    if(edate == undefined) edate = Date.now();
    if(sdate == undefined) sdate = edate - oneDayMsec;
    if(sdate > edate)
        sdate = edate - oneDayMsec;
    if(edate == sdate)
        edate = sdate + oneDayMsec;
    data.edate = edate;
    data.sdate = sdate;
    return;
}

